import React, { useState, useEffect, useContext, useCallback } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';
import { Bell, AlertTriangle, CheckCircle, Clock, Eye, Trash2, Filter, Download } from 'lucide-react';
import toast from 'react-hot-toast';

const Notifications = () => {
  const { token } = useContext(AuthContext);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    tipo: '',
    leida: '',
    prioridad: ''
  });
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    totalPages: 0
  });
  const [selectedNotification, setSelectedNotification] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [statistics, setStatistics] = useState({
    total: 0,
    noLeidas: 0,
    alta: 0,
    media: 0,
    baja: 0
  });

  const fetchNotifications = useCallback(async () => {
    try {
      setLoading(true);
      console.log('Token disponible:', !!token);
      console.log('Token value:', token);

      const queryParams = new URLSearchParams({
        page: pagination.page,
        limit: pagination.limit,
        ...Object.fromEntries(Object.entries(filters).filter(([_, v]) => v !== ''))
      });

      const url = `/api/notifications?${queryParams}`;
      console.log('URL de petición:', url);

      const response = await fetch(url, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('Response status:', response.status);
      console.log('Response ok:', response.ok);

      if (response.ok) {
        const data = await response.json();
        console.log('Data recibida:', data);
        setNotifications(data.notifications || []);
        setPagination(prev => ({
          ...prev,
          total: data.total || 0,
          totalPages: data.totalPages || 0
        }));
      } else {
        const errorData = await response.text();
        console.log('Error response:', errorData);
        toast.error('Error al cargar las notificaciones');
      }
    } catch (error) {
      console.error('Error fetching notifications:', error);
      toast.error('Error al cargar las notificaciones');
    } finally {
      setLoading(false);
    }
  }, [token, pagination.page, pagination.limit, filters]);

  const fetchStatistics = useCallback(async () => {
    try {
      const response = await fetch('/api/notifications/statistics', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setStatistics(data);
      }
    } catch (error) {
      console.error('Error fetching statistics:', error);
    }
  }, [token]);

  useEffect(() => {
    fetchNotifications();
    fetchStatistics();
  }, [fetchNotifications, fetchStatistics]);

  const markAsRead = async (notificationId) => {
    try {
      const response = await fetch(`/api/notifications/${notificationId}/read`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        setNotifications(prev => 
          prev.map(notif => 
            notif._id === notificationId 
              ? { ...notif, leida: true, fechaLectura: new Date() }
              : notif
          )
        );
        fetchStatistics();
        toast.success('Notificación marcada como leída');
      } else {
        toast.error('Error al marcar como leída');
      }
    } catch (error) {
      console.error('Error marking as read:', error);
      toast.error('Error al marcar como leída');
    }
  };

  const markAllAsRead = async () => {
    try {
      const response = await fetch('/api/notifications/mark-all-read', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        setNotifications(prev => 
          prev.map(notif => ({ 
            ...notif, 
            leida: true, 
            fechaLectura: new Date() 
          }))
        );
        fetchStatistics();
        toast.success('Todas las notificaciones marcadas como leídas');
      } else {
        toast.error('Error al marcar todas como leídas');
      }
    } catch (error) {
      console.error('Error marking all as read:', error);
      toast.error('Error al marcar todas como leídas');
    }
  };

  const deleteNotification = async (notificationId) => {
    if (!window.confirm('¿Está seguro de que desea eliminar esta notificación?')) {
      return;
    }

    try {
      const response = await fetch(`/api/notifications/${notificationId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        setNotifications(prev => prev.filter(notif => notif._id !== notificationId));
        fetchStatistics();
        toast.success('Notificación eliminada');
      } else {
        toast.error('Error al eliminar la notificación');
      }
    } catch (error) {
      console.error('Error deleting notification:', error);
      toast.error('Error al eliminar la notificación');
    }
  };

  const handleViewDetails = (notification) => {
    setSelectedNotification(notification);
    setShowModal(true);
    if (!notification.leida) {
      markAsRead(notification._id);
    }
  };

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({ ...prev, [field]: value }));
    setPagination(prev => ({ ...prev, page: 1 }));
  };

  const clearFilters = () => {
    setFilters({
      tipo: '',
      leida: '',
      prioridad: ''
    });
    setPagination(prev => ({ ...prev, page: 1 }));
  };

  const exportNotifications = async () => {
    try {
      const queryParams = new URLSearchParams({
        ...Object.fromEntries(Object.entries(filters).filter(([_, v]) => v !== ''))
      });

      const response = await fetch(`/api/notifications/export?${queryParams}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.style.display = 'none';
        a.href = url;
        a.download = `notificaciones_${new Date().toISOString().split('T')[0]}.xlsx`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        toast.success('Notificaciones exportadas exitosamente');
      } else {
        toast.error('Error al exportar las notificaciones');
      }
    } catch (error) {
      console.error('Error exporting notifications:', error);
      toast.error('Error al exportar las notificaciones');
    }
  };

  const getPriorityIcon = (priority) => {
    switch (priority) {
      case 'alta':
        return <AlertTriangle className="notification-priority-icon high" />;
      case 'media':
        return <Clock className="notification-priority-icon medium" />;
      case 'baja':
        return <CheckCircle className="notification-priority-icon low" />;
      default:
        return <Bell className="notification-priority-icon" />;
    }
  };

  const getPriorityClass = (priority) => {
    switch (priority) {
      case 'alta':
        return 'priority-high';
      case 'media':
        return 'priority-medium';
      case 'baja':
        return 'priority-low';
      default:
        return '';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading && notifications.length === 0) {
    return <LoadingSpinner message="Cargando notificaciones..." />;
  }

  return (
    <div className="notifications-container">
      <div className="notifications-header">
        <div className="header-content">
          <h1>
            <Bell className="page-icon" />
            Notificaciones
          </h1>
          <div className="header-actions">
            <button 
              onClick={markAllAsRead}
              className="btn btn-secondary"
              disabled={statistics.noLeidas === 0}
            >
              Marcar todas como leídas
            </button>
            <button onClick={exportNotifications} className="btn btn-primary">
              <Download size={16} />
              Exportar
            </button>
          </div>
        </div>

        {/* Statistics */}
        <div className="statistics-grid">
          <div className="stat-card">
            <div className="stat-value">{statistics.total}</div>
            <div className="stat-label">Total</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">{statistics.noLeidas}</div>
            <div className="stat-label">No Leídas</div>
          </div>
          <div className="stat-card priority-high">
            <div className="stat-value">{statistics.alta}</div>
            <div className="stat-label">Alta Prioridad</div>
          </div>
          <div className="stat-card priority-medium">
            <div className="stat-value">{statistics.media}</div>
