                <div className="notification-content">
                  <div className="notification-header">
                    <div className="notification-title">
                      {getPriorityIcon(notification.prioridad)}
                      <span>{notification.titulo}</span>
                      {!notification.leida && <div className="unread-indicator"></div>}
                    </div>
                    <div className="notification-date">
                      {formatDate(notification.fechaCreacion)}
                    </div>
                  </div>

                  <div className="notification-message">
                    {notification.mensaje}
                  </div>

                  <div className="notification-meta">
                    <span className={`notification-type ${notification.tipo}`}>
                      {notification.tipo}
                    </span>
                    <span className={`notification-priority ${notification.prioridad}`}>
                      {notification.prioridad}
                    </span>
                  </div>
                </div>

                <div className="notification-actions">
                  <button
                    onClick={() => handleViewDetails(notification)}
                    className="btn btn-sm btn-outline"
                    title="Ver detalles"
                  >
                    <Eye size={16} />
                  </button>
                  <button
                    onClick={() => deleteNotification(notification._id)}
                    className="btn btn-sm btn-danger"
                    title="Eliminar"
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
            ))}

            {/* Pagination */}
            {pagination.totalPages > 1 && (
              <div className="pagination">
                <button
                  onClick={() => setPagination(prev => ({ ...prev, page: prev.page - 1 }))}
                  disabled={pagination.page === 1}
                  className="btn btn-outline"
                >
                  Anterior
                </button>

                <span className="pagination-info">
                  Página {pagination.page} de {pagination.totalPages}
                </span>

                <button
                  onClick={() => setPagination(prev => ({ ...prev, page: prev.page + 1 }))}
                  disabled={pagination.page === pagination.totalPages}
                  className="btn btn-outline"
                >
                  Siguiente
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {/* Modal for notification details */}
      {showModal && selectedNotification && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
