                )}

                {selectedNotification.datos && (
                  <div className="detail-row">
                    <strong>Datos Adicionales:</strong>
                    <pre>{JSON.stringify(selectedNotification.datos, null, 2)}</pre>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

